<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Seo{    function __construct()    {        $this->CI = & get_instance();        $this->CI->load->model('seo_model');    }    function get_static_seo($url)    {        $mas_url = explode('/', $url);        if ($mas_url[0]=='search') {            $url = $mas_url[0];        }        return $this->CI->seo_model->get_static_seo($url);    }}