<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Seo{    function __construct()    {        $this->CI = & get_instance();        $this->CI->load->model('seo_model');    }    function get_static_seo($url)    {        return $this->CI->seo_model->get_static_seo($url);    }    function get_seo_dynamic($table, $id_object) {        return $this->CI->seo_model->get_seo_dynamic($table, $id_object);    }    function get_seo_dynamic_v2($table, $id_object) {        return $this->CI->seo_model->get_seo_dynamic_v2($table, $id_object);    }}