<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');    class Seo_model extends CI_Model {        function get_static_seo($url) {           $this->db->where("url",$url);           return $this->db->get('seo_static')->row_array();        }        function seoGetAll() {            return $this->db->get('seo_static')->result_array();        }    }